import React, { FC, useState } from "react";
import Button from "../UI/Button/Button";
import Input from "../UI/Input/Input";

import "./registration.scss";

interface RegistrationProps {
  isRegistrated: boolean;
  setIsRegistrated: (value: boolean) => void;
}

const Registration: FC<RegistrationProps> = ({
  isRegistrated,
  setIsRegistrated,
}) => {
  const [inputData, setInputData] = useState({
    username: "",
    email: "",
  });

  const getAccess = () => {
    if (!inputData.username || !inputData.email) {
      alert("Fill the form");
    } else {
      setIsRegistrated(true);
    }
  };
  return (
    <div className="registration">
      <h3>Beta test registration</h3>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
        tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
        veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
        commodo consequat.
      </p>
      <div className="input">
        <div className="input__subtitile">Name</div>
        {!isRegistrated ? (
          <Input placeholder="We will display your name in participation list " />
        ) : (
          <h3>{}</h3>
        )}
      </div>
      <div className="input">
        <div className="input__subtitile">Email</div>
        {!isRegistrated ? <Input /> : <h3>{}</h3>}
      </div>
      {!isRegistrated ? (
        <Button onClick={() => getAccess()}>Get early access</Button>
      ) : (
        <h3>{}</h3>
      )}
    </div>
  );
};

export default Registration;
